import React, { Component } from "react";
import "./css/Body.css";
import UploadButtons from "./UploadButtons";
import LinearDeterminate from "./LinearDeterminate";
import ContainedButtons from "./ContainedButtons";
import axios from "axios";


export default class Body extends Component {
  constructor(props) {
    super(props);
    this.state = {
      images: "", // image show in the border
      result: false,
      upload: true,
      resultImage: "" //image get from API
    };
    this.getDatas = this.getDatas.bind(this);
    this.getBack = this.getBack.bind(this);
  }
  async getDatas() {
    try {
      const dataImage = await axios.get(
        "http://localhost:5000/get-cut-image"
      );
      const byteCharacters = atob(dataImage.data);
      const byteNumbers = new Array(byteCharacters.length);
      for (let i = 0; i < byteCharacters.length; i++) {
          byteNumbers[i] = byteCharacters.charCodeAt(i);
      }
      const byteArray = new Uint8Array(byteNumbers);

      let result = new Blob([byteArray], { type: 'image/png' });
      let imageUrl = URL.createObjectURL(result);

      this.setState({
        images: imageUrl,
      });
    } catch (error) {
      console.log(error);
    }
    this.setState({result: true, upload: false});

  }
  getBack(){
    this.setState({result: false, upload: true});
  }
  render() {
    return (
      <div className="Body">

            <div>
              <UploadButtons></UploadButtons>
              <LinearDeterminate onclick={this.getDatas}></LinearDeterminate>
            </div>


            <div>
              <ContainedButtons images={this.state.images}/>
              <img src={this.state.images}/>
            </div>

      </div>
    );
  }
}
