import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import PhotoCamera from "@material-ui/icons/PhotoCamera";
import "./css/Upload.css";
import React, { Component } from "react";
import axios from "axios";

class UploadButtons extends Component {
  constructor() {
    super();
  }
  state = {
    // Initially, no file is selected
    selectedFile: null,
  };
  // On file select (from the pop up)
  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };
  // On file upload (click the upload button)
  onFileUpload = () => {
    // Create an object of formData
    const formData = new FormData();
    // Update the formData object
    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );
    // Details of the uploaded file
    console.log(this.state.selectedFile);
    // Request made to the backend api
    // Send formData object
    axios({
      method: 'post',
      url: 'http://localhost:5000/predict',
      data: formData,
      config: { headers: { 'Content-Type': 'multipart/form-data' } }
  })
      .then(response => console.log(response))
      .catch(errors => console.log(errors))  };
  render() {
    return (
      <div>
        <input
          accept="image/*"
          id="contained-button-file"
          multiple
          type="file"
          onChange={this.onFileChange}
        />
        {/* <label htmlFor="contained-button-file">
          <Button
            variant="contained"
            color="primary"
            component="span"
            onClick={this.onFileUpload}
          >
            Upload
          </Button>
        </label> */}

        <form action="http://localhost:5000/predict" method="post" encType="multipart/form-data">
                <div class="form-group">
                    <input type="file" class="form-control-file" onChange={this.onFileChange} />
                    <img src={this.state.file} class="img-thumbnail" alt="" />
                    <button type="submit" class="btn btn-outline-dark" onClick={this.onFileUpload}> Upload Photo </button>
                </div>
          </form>
        <label htmlFor="icon-button-file">
          <IconButton
            color="primary"
            aria-label="upload picture"
            component="span"
          >
            <PhotoCamera />
          </IconButton>
        </label>
        <div className="imgPreview"></div>
      </div>
    );
  }
}

export default UploadButtons;
