import React, { Component } from "react";
import "./css/Body.css";
import UploadButtons from "./UploadButtons";
import LinearDeterminate from "./LinearDeterminate";
import ContainedButtons from "./ContainedButtons";
import axios from "axios";

export default class Body extends Component {
  constructor(props) {
    super(props);
    this.state = {
      images: "", // image show in the border
      result: false,
      upload: true,
      resultImage: "" //image get from API
    };
    this.getDatas = this.getDatas.bind(this);
    this.getBack = this.getBack.bind(this);
  }
  async getDatas() {
    try {
      const dataImage = await axios.get(
        "http://127.0.0.1:5000/get-cut-image"
      );
      let buffer=Uint8Array.from(atob(dataImage.data), c => c.charCodeAt(0));
let blob=new Blob([buffer], { type: "image/gif" });
let url=URL.createObjectURL(blob);
console.log(url);
      this.setState({
        images: url,
      });
      console.log(this.state.images)
    } catch (error) {
      console.log(error);
    }
    this.setState({ result: true, upload: false });
  }

  getBack(){
    this.setState({result: false, upload: true});
  }
  render() {
    return (
      <div className="Body">

            <div>
              <UploadButtons></UploadButtons>
              <LinearDeterminate onclick={this.getDatas}></LinearDeterminate>
            </div>


            <div>
              <ContainedButtons images={this.state.images}/>
            </div>

      </div>
    );
  }
}
