import React, { Component } from "react";
import "./css/Body.css";
import Box from "@material-ui/core/Box";
import { Row, Col } from "antd";
import UploadButtons from "./UploadButtons";
import LinearDeterminate from "./LinearDeterminate";
import ContainedButtons from "./ContainedButtons";
import axios from "axios";

export default class Body extends Component {
  constructor(props) {
    super(props);
    this.state = {
      images: "",
    };
    this.getDatas = this.getDatas.bind(this);
  }
  getDatas() {
    /*try {
      const path = "http://localhost:5000/get-cut-image"
      const dataImage = axios.get(path);
      console.log(dataImage);
      //this.setState({
        //images: dataImage.data,
      //});
    } catch (error) {
      console.log(error);
    }*/
    axios.get('http://localhost:5000/get-cut-image')
      .then(function (response) {
        // handle success
        console.log(response);
        this.setState({
          images: response.data,
        });
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
  }
  render() {
    return (
      <div className="Body">
        <Row /**3 border cut, paste, process bar */>
          <Col span={7}>
            <UploadButtons></UploadButtons>
          </Col>
          <Col span={10}>
            <LinearDeterminate onclick={this.getDatas}></LinearDeterminate>
            <img src={this.state.images} alt="img" />

          </Col>
          <Col span={7}>
            <ContainedButtons images={this.state.images} />
          </Col>
        </Row>
      </div>
    );
  }
}
